    $mdDialog.show(
      $mdDialog.alert()
        .parent(angular.element($document[0].body))
        .clickOutsideToClose(true)
        .title(`Unable to display info for ${entityName}`)
        .textContent(content)
        .ariaLabel('Action Demo Dialog')
        .ok('Close')
        .targetEvent($event)
    );
}

